<?phpfunction jejuu($a){    return json_encode($a,JSON_UNESCAPED_UNICODE);}function callback_jejuu($a,$b){    if(isset($b['callback'])){        return $b['callback'].'('.jejuu($a).')';    }else{        return jejuu($a);    }}function byte_to_int($arr,$k){    //return $arr[$k] + 256 * $arr[$k + 1] + 65536 * $arr[$k + 2] + 16777216 * $arr[$k + 3];    $r = $arr[$k + 3] & 0xff;    $r <<= 8;    $r |= $arr[$k + 2] & 0xff;    $r <<= 8;    $r |= $arr[$k + 1] & 0xff;    $r <<= 8;    $r |= $arr[$k] & 0xff;    return $r;}